        vector<int> Protons_ind = GetGoodProtons(apply_nucleon_cuts, protons, IDProtons_ind,
                                                 Theta_p1_cuts_2p, Theta_p2_cuts_2p, dphi_pFD_pCD_2p); // good identified protons (no sCTOFhp and no dCDaFDd)

