            double Vx_e_pFDpCD = e_pFDpCD->par()->getVx(), Vy_e_pFDpCD = e_pFDpCD->par()->getVy(), Vz_e_pFDpCD = e_pFDpCD->par()->getVz();

            /* Setting Q2 */
            TLorentzVector e_out_pFDpCD, Q_pFDpCD;
            double Q2_pFDpCD;
